// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IIssuerRegistry.sol";

/**
 * @title IssuerRegistry
 * @dev Implementation of the issuer registry for verifying certificate issuers
 */
contract IssuerRegistry is IIssuerRegistry, Ownable {
    // Mapping from issuer address to issuer data
    mapping(address => IssuerData) private _issuers;
    
    // Mapping from hashed domain (keccak256(lowercase)) to issuer address
    mapping(bytes32 => address) private _domainToIssuer;
    
    // Mapping from hashed social media URL to issuer address
    mapping(bytes32 => address) private _socialMediaToIssuer;
    
    // Admin addresses for manual verification
    mapping(address => bool) private _admins;
    
    /**
     * @dev Emitted when an admin is added.
     */
    event AdminAdded(address indexed admin);

    /**
     * @dev Emitted when an admin is removed.
     */
    event AdminRemoved(address indexed admin);

    /**
     * @dev Emitted when an issuer is deactivated or reactivated.
     */
    event IssuerStatusChanged(address indexed issuer, bool isActive);

    /**
     * @dev Helper to compute a case-insensitive hash of a string.
     */
    function _hash(string memory input) private pure returns (bytes32) {
        return keccak256(bytes(_toLower(input)));
    }

    /**
     * @dev Converts ASCII string to lower-case (English letters only).
     */
    function _toLower(string memory str) private pure returns (string memory) {
        bytes memory bStr = bytes(str);
        bytes memory bLower = new bytes(bStr.length);
        for (uint256 i = 0; i < bStr.length; i++) {
            // Uppercase character...
            if (bStr[i] >= 0x41 && bStr[i] <= 0x5A) {
                bLower[i] = bytes1(uint8(bStr[i]) + 32);
            } else {
                bLower[i] = bStr[i];
            }
        }
        return string(bLower);
    }
    
    /**
     * @dev Constructor for IssuerRegistry
     */
    constructor() Ownable(msg.sender) {
        // Add deployer as admin
        _admins[msg.sender] = true;
    }
    
    /**
     * @dev Modifier to restrict function access to admins
     */
    modifier onlyAdmin() {
        require(_admins[msg.sender], "IssuerRegistry: Caller is not an admin");
        _;
    }
    
    /**
     * @dev Add an admin
     * @param admin The address to add as admin
     */
    function addAdmin(address admin) external onlyOwner {
        _admins[admin] = true;
        emit AdminAdded(admin);
    }
    
    /**
     * @dev Remove an admin
     * @param admin The address to remove as admin
     */
    function removeAdmin(address admin) external onlyOwner {
        _admins[admin] = false;
        emit AdminRemoved(admin);
    }
    
    /**
     * @dev Check if an address is admin.
     */
    function isAdmin(address addr) external view returns (bool) {
        return _admins[addr];
    }
    
    /**
     * @dev Verifies an issuer with T1 verification (DNS)
     * @param issuerName The name of the issuer organization
     * @param domain The domain name used for verification
     * @param verificationData Additional data used in verification process
     */
    function verifyIssuerT1(
        string calldata issuerName,
        string calldata domain,
        string calldata verificationData
    ) external override {
        // In a real implementation, this would involve checking DNS TXT records
        // For this demo, we'll simulate the verification
        
        bytes32 domainHash = _hash(domain);
        // Ensure domain isn't already registered
        require(_domainToIssuer[domainHash] == address(0), "IssuerRegistry: Domain already registered");
        
        // Create issuer data
        _issuers[msg.sender] = IssuerData({
            issuerAddress: msg.sender,
            tier: IssuerTier.T1,
            name: issuerName,
            verificationData: verificationData,
            verifiedAt: block.timestamp,
            isActive: true
        });
        
        // Register domain
        _domainToIssuer[domainHash] = msg.sender;
        
        emit IssuerVerified(msg.sender, IssuerTier.T1, issuerName);
    }
    
    /**
     * @dev Verifies an issuer with T2 verification (social media)
     * @param issuerName The name of the issuer organization
     * @param socialMediaUrl The URL of the social media post used for verification
     */
    function verifyIssuerT2(
        string calldata issuerName,
        string calldata socialMediaUrl
    ) external override {
        // In a real implementation, this would involve checking the social media post
        // For this demo, we'll simulate the verification
        
        bytes32 urlHash = _hash(socialMediaUrl);
        require(_socialMediaToIssuer[urlHash] == address(0), "IssuerRegistry: Social media URL already registered");
        
        // Create issuer data
        _issuers[msg.sender] = IssuerData({
            issuerAddress: msg.sender,
            tier: IssuerTier.T2,
            name: issuerName,
            verificationData: socialMediaUrl,
            verifiedAt: block.timestamp,
            isActive: true
        });
        
        // Register social media URL
        _socialMediaToIssuer[urlHash] = msg.sender;
        
        emit IssuerVerified(msg.sender, IssuerTier.T2, issuerName);
    }
    
    /**
     * @dev Manually verifies an issuer (only admin)
     * @param issuerAddress The address to be verified
     * @param issuerName The name of the issuer organization
     */
    function manualVerifyIssuer(
        address issuerAddress,
        string calldata issuerName
    ) external override onlyAdmin {
        // Create issuer data
        _issuers[issuerAddress] = IssuerData({
            issuerAddress: issuerAddress,
            tier: IssuerTier.MANUAL,
            name: issuerName,
            verificationData: "Manual verification",
            verifiedAt: block.timestamp,
            isActive: true
        });
        
        emit IssuerVerified(issuerAddress, IssuerTier.MANUAL, issuerName);
    }
    
    /**
     * @dev Upgrades an issuer from T2 to T1
     * @param domain The domain name used for verification
     * @param verificationData Additional data used in verification process
     */
    function upgradeToT1(
        string calldata domain,
        string calldata verificationData
    ) external override {
        // Ensure caller is a T2 issuer
        require(_issuers[msg.sender].tier == IssuerTier.T2, "IssuerRegistry: Caller is not a T2 issuer");
        
        bytes32 domainHash = _hash(domain);
        // Ensure domain isn't already registered
        require(_domainToIssuer[domainHash] == address(0), "IssuerRegistry: Domain already registered");
        
        // Store old data for event
        IssuerTier oldTier = _issuers[msg.sender].tier;
        string memory oldVerificationData = _issuers[msg.sender].verificationData;
        
        // Update issuer data
        _issuers[msg.sender].tier = IssuerTier.T1;
        _issuers[msg.sender].verificationData = verificationData;
        _issuers[msg.sender].verifiedAt = block.timestamp;
        
        // Register domain
        _domainToIssuer[domainHash] = msg.sender;
        
        // Unregister social media URL
        bytes32 oldHash = _hash(oldVerificationData);
        _socialMediaToIssuer[oldHash] = address(0);
        
        emit IssuerUpgraded(msg.sender, oldTier, IssuerTier.T1);
    }
    
    /**
     * @dev Checks if an address is a verified issuer
     * @param issuer The address to check
     * @return True if the address is a verified issuer, false otherwise
     */
    function isVerifiedIssuer(address issuer) external view override returns (bool) {
        return _issuers[issuer].tier != IssuerTier.NONE && _issuers[issuer].isActive;
    }
    
    /**
     * @dev Gets the tier level of an issuer
     * @param issuer The address to check
     * @return The tier level of the issuer
     */
    function getIssuerTier(address issuer) external view override returns (IssuerTier) {
        return _issuers[issuer].tier;
    }
    
    /**
     * @dev Gets the data for an issuer
     * @param issuer The address to get data for
     * @return The issuer data
     */
    function getIssuerData(address issuer) external view override returns (IssuerData memory) {
        return _issuers[issuer];
    }
    
    /**
     * @dev Checks if a domain is registered
     * @param domain The domain to check
     * @return The address associated with the domain, or address(0) if not registered
     */
    function getDomainIssuer(string calldata domain) external view returns (address) {
        return _domainToIssuer[_hash(domain)];
    }
    
    /**
     * @dev Checks if a social media URL is registered
     * @param socialMediaUrl The social media URL to check
     * @return The address associated with the social media URL, or address(0) if not registered
     */
    function getSocialMediaIssuer(string calldata socialMediaUrl) external view returns (address) {
        return _socialMediaToIssuer[_hash(socialMediaUrl)];
    }
    
    /**
     * @dev Deactivates an issuer
     * @param issuer The address to deactivate
     */
    function deactivateIssuer(address issuer) external onlyAdmin {
        require(_issuers[issuer].tier != IssuerTier.NONE, "IssuerRegistry: Not a registered issuer");
        _issuers[issuer].isActive = false;
        emit IssuerStatusChanged(issuer, false);
    }
    
    /**
     * @dev Reactivates an issuer
     * @param issuer The address to reactivate
     */
    function reactivateIssuer(address issuer) external onlyAdmin {
        require(_issuers[issuer].tier != IssuerTier.NONE, "IssuerRegistry: Not a registered issuer");
        _issuers[issuer].isActive = true;
        emit IssuerStatusChanged(issuer, true);
    }
}

contracts/SoulboundNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./interfaces/ISoulboundNFT.sol";
import "./interfaces/IIssuerRegistry.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title SoulboundNFT
 * @dev Implementation of non-transferable (soulbound) NFTs for certificates
 */
contract SoulboundNFT is ERC721, Ownable, ISoulboundNFT {
    using Strings for uint256;
    
    // Token counter for NFT IDs
    uint256 private _tokenIdCounter;
    
    // Certificate data
    struct Certificate {
        address issuer;
        uint8 issuerLevel;
        string metadataURI;
        uint256 issuedAt;
        address recipient;
    }
    
    // Mapping from token ID to certificate data
    mapping(uint256 => Certificate) private _certificates;
    
    // Merkle root for whitelisting recipients
    bytes32 private _merkleRoot;
    
    // Address of the issuer registry contract
    address private _issuerRegistry;
    
    // Base URI for metadata
    string private _baseTokenURI;
    
    // Mapping from recipient to has-certificate flag to prevent duplicates
    mapping(address => bool) private _hasCertificate;
    
    // Error for soulbound transfers
    error SoulboundToken();
    
    event BaseURIUpdated(string oldBaseURI, string newBaseURI);
    event MerkleRootUpdated(bytes32 oldRoot, bytes32 newRoot);
    
    /**
     * @dev Constructor for the SoulboundNFT contract
     * @param name Name of the NFT collection
     * @param symbol Symbol of the NFT collection
     * @param merkleRoot Merkle root for whitelisting recipients
     * @param issuerRegistry Address of the issuer registry contract
     * @param baseTokenURI Base URI for metadata
     */
    constructor(
        string memory name,
        string memory symbol,
        bytes32 merkleRoot,
        address issuerRegistry,
        string memory baseTokenURI
    ) ERC721(name, symbol) Ownable(msg.sender) {
        _merkleRoot = merkleRoot;
        _issuerRegistry = issuerRegistry;
        _baseTokenURI = baseTokenURI;
    }
    
    /**
     * @dev Override _update to prevent transfers (except minting and burning)
     */
    function _update(address to, uint256 tokenId, address auth) internal override returns (address) {
        address from = _ownerOf(tokenId);
        
        // Allow minting (from == address(0)) and burning (to == address(0))
        // but prevent transfers (from != address(0) && to != address(0))
        if (from != address(0) && to != address(0)) {
            revert SoulboundToken();
        }
        
        return super._update(to, tokenId, auth);
    }
    
    /**
     * @dev Alias for mintCertificate to maintain compatibility with frontend
     * @param recipient The address that will receive the certificate
     * @param merkleProof The Merkle proof that verifies the recipient is eligible
     * @return The token ID of the minted certificate
     */
    function mint(address recipient, bytes32[] calldata merkleProof) external returns (uint256) {
        return mintCertificate(recipient, merkleProof);
    }
    
    /**
     * @dev Mints a new certificate to a recipient
     * @param recipient The address that will receive the certificate
     * @param merkleProof The Merkle proof that verifies the recipient is eligible
     * @return The token ID of the minted certificate
     */
    function mintCertificate(address recipient, bytes32[] calldata merkleProof) public override returns (uint256) {
        // Verify issuer is authorized
        require(
            IIssuerRegistry(_issuerRegistry).isVerifiedIssuer(msg.sender),
            "SoulboundNFT: Caller is not a verified issuer"
        );
        
        // Verify recipient is in the whitelist
        require(
            isEligible(recipient, merkleProof),
            "SoulboundNFT: Recipient is not eligible"
        );
        
        // Verify recipient does not already possess a certificate
        require(!_hasCertificate[recipient], "SoulboundNFT: Recipient already possesses a certificate");
        
        // Get issuer level
        IIssuerRegistry.IssuerTier issuerTier = IIssuerRegistry(_issuerRegistry).getIssuerTier(msg.sender);
        
        // Increment token ID
        _tokenIdCounter++;
        uint256 tokenId = _tokenIdCounter;
        
        // Mint NFT
        _mint(recipient, tokenId);
        
        // Store certificate data
        _certificates[tokenId] = Certificate({
            issuer: msg.sender,
            issuerLevel: uint8(issuerTier),
            metadataURI: string(abi.encodePacked(_baseTokenURI, tokenId.toString())),
            issuedAt: block.timestamp,
            recipient: recipient
        });
        
        // Mark recipient as having a cert
        _hasCertificate[recipient] = true;
        
        emit CertificateMinted(recipient, tokenId, msg.sender);
        
        return tokenId;
    }
    
    /**
     * @dev Checks if an address is eligible to claim a certificate
     * @param recipient The address to check eligibility for
     * @param merkleProof The Merkle proof to verify eligibility
     * @return True if the address is eligible, false otherwise
     */
    function isEligible(address recipient, bytes32[] calldata merkleProof) public view override returns (bool) {
        bytes32 leaf = keccak256(abi.encodePacked(recipient));
        return MerkleProof.verify(merkleProof, _merkleRoot, leaf);
    }
    
    /**
     * @dev Gets the certificate data for a token ID
     * @param tokenId The token ID to get data for
     * @return issuer The address of the certificate issuer
     * @return issuerLevel The level of the issuer (T1, T2, etc.)
     * @return metadataURI The URI for the certificate metadata
     * @return issuedTimestamp The timestamp when the certificate was issued
     */
    function getCertificateData(uint256 tokenId) external view override returns (
        address issuer,
        uint8 issuerLevel,
        string memory metadataURI,
        uint256 issuedTimestamp
    ) {
        require(_exists(tokenId), "SoulboundNFT: Query for nonexistent token");
        
        Certificate memory cert = _certificates[tokenId];
        
        return (
            cert.issuer,
            cert.issuerLevel,
            cert.metadataURI,
            cert.issuedAt
        );
    }
    
    /**
     * @dev Gets the issuer of a token
     * @param tokenId The token ID
     * @return The address of the issuer
     */
    function issuerOf(uint256 tokenId) external view returns (address) {
        require(_exists(tokenId), "SoulboundNFT: Query for nonexistent token");
        return _certificates[tokenId].issuer;
    }
    
    /**
     * @dev Gets the issuance timestamp of a token
     * @param tokenId The token ID
     * @return The timestamp when the token was issued
     */
    function getIssuedAt(uint256 tokenId) external view returns (uint256) {
        require(_exists(tokenId), "SoulboundNFT: Query for nonexistent token");
        return _certificates[tokenId].issuedAt;
    }
    
    /**
     * @dev Gets the URI for a token's metadata
     * @param tokenId The token ID to get the URI for
     * @return The token URI
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "SoulboundNFT: URI query for nonexistent token");
        return _certificates[tokenId].metadataURI;
    }
    
    /**
     * @dev Updates the base token URI
     * @param newBaseURI The new base token URI
     */
    function setBaseURI(string memory newBaseURI) external onlyOwner {
        string memory old = _baseTokenURI;
        _baseTokenURI = newBaseURI;
        emit BaseURIUpdated(old, newBaseURI);
    }
    
    /**
     * @dev Updates the Merkle root
     * @param newMerkleRoot The new Merkle root
     */
    function setMerkleRoot(bytes32 newMerkleRoot) external onlyOwner {
        bytes32 old = _merkleRoot;
        _merkleRoot = newMerkleRoot;
        emit MerkleRootUpdated(old, newMerkleRoot);
    }
    
    /**
     * @dev Gets the current Merkle root
     * @return The current Merkle root
     */
    function getMerkleRoot() external view returns (bytes32) {
        return _merkleRoot;
    }
    
    /**
     * @dev Utility function to check if a token exists
     * @param tokenId The token ID to check
     * @return True if the token exists, false otherwise
     */
    function _exists(uint256 tokenId) internal view returns (bool) {
        return tokenId != 0 && tokenId <= _tokenIdCounter && _ownerOf(tokenId) != address(0);
    }
    
    /**
     * @dev Overrides approve to block approvals for soul-bound tokens.
     */
    function approve(address, uint256) public pure override {
        revert SoulboundToken();
    }

    /**
     * @dev Overrides setApprovalForAll to block operator approvals.
     */
    function setApprovalForAll(address, bool) public pure override {
        revert SoulboundToken();
    }

    /**
     * @dev Overrides getApproved to always return zero address.
     */
    function getApproved(uint256) public pure override returns (address) {
        return address(0);
    }

    /**
     * @dev Burns a certificate. Callable by the token owner or contract owner.
     * Clearing the recipient flag enables re-issuing if needed.
     */
    function burn(uint256 tokenId) external {
        address tokenOwner = _ownerOf(tokenId);
        require(
            msg.sender == tokenOwner || msg.sender == owner(),
            "SoulboundNFT: Not authorised to burn"
        );
        _burn(tokenId);
        _hasCertificate[tokenOwner] = false;
    }
}

contracts/ZKVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IZKVerifier.sol";

/**
 * @title ZKVerifier
 * @dev Contract for managing zero-knowledge proof verification for certificates
 */
contract ZKVerifier is IZKVerifier, Ownable {
    // Counter for circuit IDs
    uint256 private _circuitIdCounter;
    
    // Counter for proof IDs
    uint256 private _proofIdCounter;
    
    // Mapping from circuit ID to circuit data
    mapping(uint256 => VerifierCircuit) private _circuits;
    
    // Mapping from proof ID to proof data
    mapping(uint256 => ProofData) private _proofs;
    
    // Mapping from owner to proof IDs
    mapping(address => uint256[]) private _ownerProofs;
    
    // Admin addresses
    mapping(address => bool) private _admins;
    
    /**
     * @dev Constructor for ZKVerifier
     */
    constructor() Ownable(msg.sender) {
        // Add deployer as admin
        _admins[msg.sender] = true;
    }
    
    /**
     * @dev Modifier to restrict function access to admins
     */
    modifier onlyAdmin() {
        require(_admins[msg.sender], "ZKVerifier: Caller is not an admin");
        _;
    }
    
    /**
     * @dev Add an admin
     * @param admin The address to add as admin
     */
    function addAdmin(address admin) external onlyOwner {
        _admins[admin] = true;
    }
    
    /**
     * @dev Remove an admin
     * @param admin The address to remove as admin
     */
    function removeAdmin(address admin) external onlyOwner {
        _admins[admin] = false;
    }
    
    /**
     * @dev Registers a new verifier circuit (admin only)
     * @param name The name of the circuit
     * @param description The description of the circuit
     * @param verifierContract The address of the verifier contract
     * @return The ID of the registered circuit
     */
    function registerCircuit(
        string calldata name,
        string calldata description,
        address verifierContract
    ) external override onlyAdmin returns (uint256) {
        require(verifierContract != address(0), "ZKVerifier: Invalid verifier contract address");
        
        // Increment circuit ID
        _circuitIdCounter++;
        uint256 circuitId = _circuitIdCounter;
        
        // Store circuit data
        _circuits[circuitId] = VerifierCircuit({
            id: circuitId,
            name: name,
            description: description,
            verifierContract: verifierContract,
            isActive: true
        });
        
        emit CircuitRegistered(circuitId, name, verifierContract);
        
        return circuitId;
    }
    
    /**
     * @dev Creates a new ZK proof
     * @param circuitId The ID of the circuit to use
     * @param proofData The raw proof data
     * @param publicInputs The public inputs for the proof
     * @param validityPeriod How long the proof should be valid for (in seconds)
     * @return The ID of the created proof
     */
    function createProof(
        uint256 circuitId,
        bytes calldata proofData,
        bytes calldata publicInputs,
        uint256 validityPeriod
    ) external override returns (uint256) {
        require(_circuits[circuitId].isActive, "ZKVerifier: Circuit is not active");
        
        // In a real implementation, we would actually verify the proof
        // using the circuit's verifier contract
        // For this demo, we'll assume the proof is valid
        
        // Increment proof ID
        _proofIdCounter++;
        uint256 proofId = _proofIdCounter;
        
        // Calculate proof hash
        bytes32 proofHash = keccak256(abi.encodePacked(proofData, publicInputs));
        
        // Calculate expiration time
        uint256 expiresAt = block.timestamp + validityPeriod;
        
        // Store proof data
        _proofs[proofId] = ProofData({
            id: proofId,
            owner: msg.sender,
            circuitId: circuitId,
            proofHash: proofHash,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            isRevoked: false
        });
        
        // Add proof to owner's list
        _ownerProofs[msg.sender].push(proofId);
        
        emit ProofCreated(proofId, msg.sender, circuitId);
        
        return proofId;
    }
    
    /**
     * @dev Convenience wrapper that skips the optional verifierAddress ACL.
     */
    function verifyProof(uint256 proofId) external view returns (bool) {
        return _isProofValid(proofId);
    }
    
    /**
     * @dev Internal validation logic shared by both verify functions.
     */
    function _isProofValid(uint256 proofId) internal view returns (bool) {
        ProofData memory proof = _proofs[proofId];
        if (proof.id == 0) return false;
        if (proof.isRevoked) return false;
        if (block.timestamp > proof.expiresAt) return false;
        return true;
    }
    
    /**
     * @dev Verifies a ZK proof
     * @param proofId The ID of the proof to verify
     * @param verifierAddress The address of the verifier (if restricted)
     * @return True if the proof is valid, false otherwise
     */
    function verifyProof(uint256 proofId, address verifierAddress) external view override returns (bool) {
        ProofData memory proof = _proofs[proofId];
        
        // Check if proof exists
        if (proof.id == 0) {
            return false;
        }
        
        // Check if proof is revoked
        if (proof.isRevoked) {
            return false;
        }
        
        // Check if proof is expired
        if (block.timestamp > proof.expiresAt) {
            return false;
        }
        
        // Optional ACL enforcement if verifierAddress specified
        if (verifierAddress != address(0) && !_admins[verifierAddress]) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @dev Revokes a ZK proof
     * @param proofId The ID of the proof to revoke
     */
    function revokeProof(uint256 proofId) external override {
        ProofData storage proof = _proofs[proofId];
        
        // Check if proof exists
        require(proof.id != 0, "ZKVerifier: Proof does not exist");
        
        // Check if caller is the owner or an admin
        require(
            proof.owner == msg.sender || _admins[msg.sender],
            "ZKVerifier: Caller is not authorized to revoke this proof"
        );
        
        // Revoke the proof
        proof.isRevoked = true;
        
        emit ProofRevoked(proofId, msg.sender);
    }
    
    /**
     * @dev Gets information about a proof
     * @param proofId The ID of the proof
     * @return The proof data
     */
    function getProofData(uint256 proofId) external view override returns (ProofData memory) {
        return _proofs[proofId];
    }
    
    /**
     * @dev Gets information about a proof (alias for frontend compatibility)
     * @param proofId The ID of the proof
     * @return The proof data
     */
    function getProof(uint256 proofId) external view returns (ProofData memory) {
        return _proofs[proofId];
    }
    
    /**
     * @dev Gets information about a circuit
     * @param circuitId The ID of the circuit
     * @return The circuit data
     */
    function getCircuitData(uint256 circuitId) external view override returns (VerifierCircuit memory) {
        return _circuits[circuitId];
    }
    
    /**
     * @dev Gets all proofs owned by an address
     * @param owner The address to get proofs for
     * @return An array of proof IDs
     */
    function getProofsByOwner(address owner) external view returns (uint256[] memory) {
        return _ownerProofs[owner];
    }
    
    /**
     * @dev Gets the number of proofs created by a user
     * @param user The address of the user
     * @return The number of proofs
     */
    function getProofCount(address user) external view returns (uint256) {
        return _ownerProofs[user].length;
    }
    
    /**
     * @dev Gets a proof ID by index in a user's proofs
     * @param user The address of the user
     * @param index The index in the user's proof array
     * @return The proof ID
     */
    function userProofByIndex(address user, uint256 index) external view returns (uint256) {
        require(index < _ownerProofs[user].length, "ZKVerifier: Index out of bounds");
        return _ownerProofs[user][index];
    }
    
    /**
     * @dev Deactivate a circuit
     * @param circuitId The ID of the circuit to deactivate
     */
    function deactivateCircuit(uint256 circuitId) external onlyAdmin {
        require(_circuits[circuitId].id != 0, "ZKVerifier: Circuit does not exist");
        _circuits[circuitId].isActive = false;
    }
    
    /**
     * @dev Reactivate a circuit
     * @param circuitId The ID of the circuit to reactivate
     */
    function reactivateCircuit(uint256 circuitId) external onlyAdmin {
        require(_circuits[circuitId].id != 0, "ZKVerifier: Circuit does not exist");
        _circuits[circuitId].isActive = true;
    }
}

